import ExpenseItem from '../ExpenseItem/ExpenseItem'
import ExpenseFilter from './ExpenseFilter'
import {useState} from 'react'

function Expenses (props){
    const {expenses}=props

    const [selectYear, setSelectYear]=useState("")

    const expensesFilterHandler=selectedYear=>{
        setSelectYear(selectedYear)
    }

    return (
        <>
    <div>
        <ExpenseFilter expensesFilter={expensesFilterHandler}/>
    </div>

        {expenses.filter(each=>{
            if (each.date.getFullYear()===selectYear){
                return (<ExpenseItem details={each} key={each.id}/>)
            }
            return null
         })}
        </>
    )
}

export default Expenses
