import './ExpenseForm.css'
import {useState} from 'react'

const ExpenseForm =()=>{

    // const [titleText, setTitleText]=useState("")
    // const [enteredAmount, setEnteredAmount]=useState("")
    // const [enteredDate, setEnteredDate]=useState("")

    const [userInput, setUserInput]=useState(
        {enteredTitleText:"",
    enteredAmount:"",
enteredDate:""}
    )

    const titleChangeHandler=event=>{
        setUserInput({
            ...userInput, enteredTitleText:event.target.value
        })
    }

    const amountChangeHandler=event=>{
        setUserInput({
            ...userInput, enteredAmount:event.target.value
        })
    }

    const dateChangeHandler=event=>{
        setUserInput({
            ...userInput, enteredDate:event.target.value
        })
    }


    return (
        <form>
<div className='new-expense__controls'>
    <div className='="new-expense__control'>
    <label id="title">Title</label>
    <input type="text" htmlFor="title" 
    onChange={titleChangeHandler}
    value={userInput.enteredTitleText}/>
    </div>

    <div className='="new-expense__control'>
    <label id="amount">Amount</label>
    <input type="number" htmlFor="amount" min="0.01" step="0.01" 
    onChange={amountChangeHandler}
    value={userInput.enteredAmount}/>
    </div>

    <div className='="new-expense__control'>
    <label id="date">Date</label>
    <input type="date" htmlFor="date" min="2019-01-01" max="2022-12-31" 
    onChange={dateChangeHandler}
    value={userInput.enteredDate}/>
    </div>

     <div className='new-expense__actions'> 
    <button type="submit" >Add Expense</button>
    </div>  

</div>



        </form>
    )
}

export default ExpenseForm
